	public partial class AnalysisGlobalModeOptionsViewController : NSViewController
	{
        public event EventHandler<Tuple<ParameterTypes, VariableConstraint>> ParameterContraintUpdated;

        ParameterTypes key;
        List<VariableConstraint> options;

        public AnalysisGlobalModeOptionsViewController (IntPtr handle) : base (handle)
		{

		}

        public AnalysisGlobalModeOptionsViewController()
        {

        }

        public void Setup(ParameterTypes type, List<VariableConstraint> options)
        {
            key = type;

            ParameterOptionControl.SegmentCount = 0;

            foreach (var opt in options)
            {
                var text = opt.GetEnumDescription();

                ParameterOptionControl.SegmentCount++;
                ParameterOptionControl.SetLabel(text, ParameterOptionControl.SegmentCount - 1);
                ParameterOptionControl.SetWidth(0, ParameterOptionControl.SegmentCount - 1);
            }
        }

        partial void ParameterOptionControlClicked(NSSegmentedControl sender)
        {
            ParameterContraintUpdated.Invoke(null, new(key, options[(int)sender.SelectedSegment]));
        }
    }
