        nfloat SegWidth
        {
            get
            {
                switch ((float)View.Frame.Width)
                {
                    case > 700: return 461;
                    default:
                        var x2 = 700 - View.Frame.Width;
                        return 461 - 1.5f * x2 * x2 / (700 + x2);
                }
            }
        }

        CoreGraphics.CGSize SegSize => new CoreGraphics.CGSize(SegWidth, 40);

